import React, { Component } from 'react';
import './app.css';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import MailGetter from '../mailers/mail-getter/mail-getter';
import MailPoster from '../mailers/mail-poster/mail-poster';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            screenToShow: <MailGetter/>,
        };
        this.showDiv = this.showDiv.bind(this)
    }

    showDiv(param){
        this.setState({
            screenToShow: param,
        })
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        console.log("hello")
    }

    componentDidMount() {
        document.getElementById("getter").addEventListener("click", this.showDiv(<MailGetter/>))
        document.getElementById("poster").addEventListener("click", this.showDiv(<MailPoster/>))
    }

    render() {
        let { screenToShow } = this.state;

        return (
            <Container>
                <Row>
                    <Col xs={{ span: 10, offset: 1 }} className="wrapper">
                        <Row>
                            <Col xs={12} className="nav-wrapper">
                                <Navbar
                                    expand="md"
                                    className="align-items-center"
                                >
                                    <Navbar.Brand>Bulk Mailer</Navbar.Brand>
                                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                                    <Navbar.Collapse
                                        id="basic-navbar-nav"
                                        className="justify-content-md-end justify-content-center"
                                    >
                                        <Button
                                            variant="outline-dark controller"
                                            id="getter"
                                        >
                                            Create Template
                                        </Button>
                                        <Button
                                            variant="outline-dark controller"
                                            id="poster"
                                        >
                                            Send Mails
                                        </Button>
                                    </Navbar.Collapse>
                                </Navbar>
                            </Col>
                            <Col
                                xs={12}
                                className="content-wrapper"
                                id="content-wrapper"
                            >
                                <React.StrictMode>
                                    {screenToShow}
                                </React.StrictMode>
                            </Col>
                        </Row>
                    </Col>
                </Row>
            </Container>
        );
    }
}

export default App;
