import React, { Component } from 'react';
import './app.css';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import MailGetter from '../mailers/mail-getter/mail-getter';
import MailPoster from '../mailers/mail-poster/mail-poster';

class App extends Component {
    state = {
        screenToShow: <MailGetter />,
    };

    showSender(){
        this.setState({
            screenToShow: <MailGetter/>
        })
    }

    showPoster(){
        this.setState({
            screenToShow: <MailPoster/>
        })
    }

    render() {
        let { screenToShow } = this.state;

        return (
            <Container>
                <Row>
                    <Col xs={{ span: 10, offset: 1 }} className="wrapper">
                        <Row>
                            <Col xs={12} className="nav-wrapper">
                                <Navbar
                                    expand="md"
                                    className="align-items-center"
                                >
                                    <Navbar.Brand>Bulk Mailer</Navbar.Brand>
                                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                                    <Navbar.Collapse
                                        id="basic-navbar-nav"
                                        className="justify-content-md-end justify-content-center"
                                    >
                                        <Button
                                            variant="outline-dark controller"
                                            onClick={function(){this.showSender}}
                                        >
                                            Create Template
                                        </Button>
                                        <Button
                                            variant="outline-dark controller"
                                            onClick={function(){this.showPoster}}
                                        >
                                            Send Mails
                                        </Button>
                                    </Navbar.Collapse>
                                </Navbar>
                            </Col>
                            <Col
                                xs={12}
                                className="content-wrapper"
                                id="content-wrapper"
                            >
                                <React.StrictMode>
                                    {screenToShow}
                                </React.StrictMode>
                            </Col>
                        </Row>
                    </Col>
                </Row>
            </Container>
        );
    }
}

export default App;
