import React, { Component } from 'react';
import Form from 'react-bootstrap/Form';
import '../mail.css'
import './mail-poster.css'

class MailPoster extends Component {
    render() {
        return (
            <div>
                <Form>
                    <Form.Group controlId="mailTitle">
                        <Form.Control type="text" placeholder="Enter mail title"/>
                    </Form.Group>
                    <Form.Group controlId="mailContent" className="mail-content">
                        <Form.Control type="text" placeholder="Enter mail content"/>
                        <Form.Text className="text-muted">
                            Variables are declared by putting them in accolades: {"{variable}"}
                        </Form.Text>
                    </Form.Group>
                </Form>
            </div>
        );
    }
}

export default MailPoster;
